example.code:
static Mercury _mercury;

   public ControlSystemDge100()
   {
       //Register console commands
       CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a Mercury CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(AllAudioToggleCmd, "AllAudioToggle", "This is a Mercury AllAudioToggle command", ConsoleAccessLevelEnum.AccessOperator);
   }

   public void CreateDeviceCmd(String args)
   {
       //Initialize the Mercury device for Ethernet Communication.
       _mercury = new Mercury(0x18, this);

       _mercury.ExtenderAuthenticationReservedSigs.Use();

       _mercury.BaseEvent += MercuryEventHandler;

       _mercury.HdmiIn.VideoAttributes.AttributeChange += Mercury_VideoAttributeEventHandler;
       _mercury.HdmiIn.StreamCec.CecChange += MercuryCecChangeEventHandler;
       _mercury.HdmiIn.StreamChange += Mercury_StreamChangeEventHandler;

       _mercury.HdmiOut.VideoAttributes.AttributeChange += Mercury_VideoAttributeEventHandler;
       _mercury.HdmiOut.StreamCec.CecChange += MercuryCecChangeEventHandler;
       _mercury.HdmiOut.StreamChange += Mercury_StreamChangeEventHandler;
       _mercury.HdmiOut.ConnectedDevice.DeviceInformationChange += Mercury_DeviceInfoChangeEventHandler;

       _mercury.UsbPort.UsbHidChange += Mercury_UsbHidChangeEventHandler;

       _mercury.AirMedia.AirMediaChange += new GenericEventHandler(Mercury_AirMedia_AirMediaChange); 

       //Reserved sig handler initialization
       _mercury.ExtenderAuthenticationReservedSigs.DeviceExtenderSigChange += ExtenderAuthenticationReservedSigs_DeviceExtenderSigChange;

       _mercury.Register();
   }

   // Method to toggle the All Audio Toggle Sig value for Mercury.
   public void AllAudioToggleCmd(String args)
   {
       if (_mercury.ExtenderAudioReservedSigs.AllAudioOnFeedback.BoolValue)
       {
           CrestronConsole.ConsoleCommandResponse("Disable Audio\r\n");
           _mercury.ExtenderAudioReservedSigs.AllAudioOff();
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Enable Audio\r\n");
           _mercury.ExtenderAudioReservedSigs.AllAudioOn();
       }
   }

   // Sig callback used to read Authentication Control Reserved Sig information received from the Mercury.
   static void ExtenderAuthenticationReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
   {
       //Sig change for the extender
       if (args.Sig == _mercury.ExtenderAuthenticationReservedSigs.AuthenticationOnFeedback)
       {
           CrestronConsole.PrintLine("AuthenticationOnFeedback: {0}", args.Sig.BoolValue);
       }
   }

   // Method to handle HDMI Out/In sig change events for Mercury Device.
   static void MercuryEventHandler(GenericBase device, BaseEventArgs args)
   {
       if (device.ID == 4)
       {
           CrestronConsole.PrintLine("HdmiIn");
       }
       else if (device.ID == 5)
       {
           CrestronConsole.PrintLine("HdmiOut");
       }

       if (args.EventId == DMInputEventIds.SourceSyncEventId)
       {
           CrestronConsole.PrintLine("Sync detected on Mercury, SyncDetectedFeedback value is {0}", _mercury.HdmiIn.SyncDetectedFeedback.BoolValue);
       }
       else if (args.EventId == DMOutputEventIds.SyncDetectedEventId)
       {
           CrestronConsole.PrintLine("Sync detected on Mercury, SyncDetectedFeedback value is {0}", _mercury.HdmiOut.SyncDetectedFeedback.BoolValue);
       }
   }

   // Method to handle USB HID sig change events for Mercury Device.
   static void Mercury_UsbHidChangeEventHandler(UsbHidPort port, UsbHidEventArgs args)
   {
       if (args.EventId == UsbHidEventIds.DeviceDetectedEventId)
       {
           CrestronConsole.PrintLine("{0} device Device Detected Feedback Event Occurred\r\n", _mercury.UsbPort.ToString());
       }
   }

   // Method to handle HDMI Out Device Info sig change events for Mercury Device.
   static void Mercury_DeviceInfoChangeEventHandler(ConnectedDeviceInformation device, ConnectedDeviceEventArgs args)
   {
       if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
       {
           CrestronConsole.PrintLine("{0} device Manufacturer Name Feedback Event Occurred\r\n", _mercury.HdmiOut.ConnectedDevice.ToString());
       }
   }

   // Method to handle HDMI Out/In Stream sig change events for Mercury Device.
   static void Mercury_StreamChangeEventHandler(Stream stream, StreamEventArgs args)
   {
       if (args.EventId == DMInputEventIds.AudioFormatEventId)
       {
           CrestronConsole.PrintLine("{0} device Audio Format Feedback Event Occurred\r\n", stream.ToString());
       }
   }

   // Method to handle HDMI Out/In VideoAttribute sig change events for Mercury Device.
   static void Mercury_VideoAttributeEventHandler(object sender, GenericEventArgs args)
   {
       if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
       {
           CrestronConsole.PrintLine("{0} device Horizontal Resolution Feedback Event Occurred\r\n", sender.ToString());
       }
   }

   // Method to handle HDMI Out/In CEC sig change events for Mercury Device.
   static void MercuryCecChangeEventHandler(Cec device, CecEventArgs args)
   {
       if (args.EventId == CecEventIds.ErrorFeedbackEventId)
       {
           CrestronConsole.PrintLine(string.Format("Device: {0} - Cec Error Detected Status: {1}", _mercury.ToString(), _mercury.HdmiOut.StreamCec.ErrorFeedback.BoolValue));
       }
   }

    public void Mercury_AirMedia_AirMediaChange(object sender, GenericEventArgs args)
    {

    }

